mapper.update:
UPDATE booklist SET book_name = #{book_name}, author = #{author}, category = #{category},
                    price = #{price},status = #{status},
                    picture = #{picture},main_picture = #{main_picture},introduce = #{introduce}
WHERE book_id = #{book_id}

mapper.select:
SELECT COUNT(*) as "value", #{category} as "category" FROM booklist WHERE category = #{category}
                                                                      AND status ="已出库"
                                                                      AND buildTime >= DATE_SUB(NOW(), INTERVAL 30 DAY)
